var article_api="api/article",article_id=$("#article_id").val();$(document).ready(function(){$(".cover-items").click(function(){var e=$(this).attr("data-cover");$.ajax({url:article_api,cache:!1,dataType:"json",type:"POST",data:{request:"set_cover",article_id:article_id,cover_id:e},error:function(e){console.log(e.responseText)}}).done(function(e){console.log(e)})}),$("#articleURL").on("blur",function(){var e=$(this).val();console.log(e),$.ajax({url:article_api,cache:!1,dataType:"json",type:"POST",data:{request:"edit_url",article_id:article_id,url:e},error:function(e){console.log(e.responseText)}}).done(function(e){console.log(e)})})});